#Assigning pin numbers to LEDs
LED_PIN_1 = 1
LED_PIN_2 = 17

#Assigning Pin numbers to sonar sensors
Sonar_TRIG1 = 16
Sonar_ECHO1 = 18

# Raspberry Pi setup
