    def retranslateUi2(self, MainWindow):
        mcp = Adafruit_MCP3008.MCP3008(clk=CLK, cs=CS, miso=MISO, mosi=MOSI)
        voltage = DataUsage.read_voltage()
        current = DataUsage.read_voltage()

        _translate = QtCore.QCoreApplication.translate
        VOL = DataUsage.read_voltage()
        VOL = round(VOL, 2)
        VOL = str(VOL)
        
        CUR = DataUsage.read_current(mcp, voltage)
        CUR = round(CUR, 2)
        CUR = str(CUR)
        
        REI = voltage / current
        REI = round(REI, 2)
        REI = str(REI)
        
        Watt = voltage * current
        Watt = round(Watt, 2)
        Watt = str(Watt)
        
        dayUse = Watt * 24
        
        
        
